  to_date(ep.epp007,'yyyy-MM-dd')- to_date(ep.epp006,'yyyy-MM-dd')
  when is_date_yy(ep.epp006)=1 and ep.epp007 is null
  then sysdate- to_date(ep.epp006,'yyyy-MM-dd')
  else 0 end)/30)) monthcount,
ep.AAE036,ep.DATASOURCE,ep.AAE100,ep.UPDATEDATE,ep.UPDATEBY,ep.CREATEBY,ep.AAE012,ep.EPP010,ep.EPP011,ep.EPP012,ep.EPP013,ep.EPP014,ep.EPP015,ep.EPP016,ep.ISNEW,
