select 

from poor_labor_force
where PLF001 = #{plf001,jdbcType=VARCHAR}

select count(1)
from poor_labor_force where aae100='1' and plf025 ='0'

  and  plf005 = #{plf005}

select
PLF001, PLF002, AAB301, PLF004, PLF005, PLF006, PLF007, PLF008, PLF009, PLF010, PLF011,phd010,
PLF012, PLF013, PLF014, AAE011, AAE036, PLF016, PLF015, PLF017, PLF018, PLF019, PLF020, PLF021, PLF022, PLF023,PLF024,PLF025,PLF026,
DATASOURCE,AAE100,UPDATEDATE,UPDATEBY,CREATEBY,AAE012,ISOVERAGE,HELPYEAR,phd013,phd014,phd014_19,rn
from (select
PLF001, PLF002, AAB301, PLF004, PLF005, PLF006, PLF007, PLF008, PLF009, PLF010, PLF011,phd010,
PLF012, PLF013, PLF014, AAE011, AAE036, PLF016, PLF015, PLF017, PLF018, PLF019, PLF020, PLF021, PLF022, PLF023,PLF024,PLF025,PLF026,
DATASOURCE,AAE100,UPDATEDATE,UPDATEBY,CREATEBY,AAE012,ISOVERAGE,HELPYEAR,phd013,phd014,phd014_19,rownum rn
from (select plf.PLF001, plf.PLF002,'' phd010,
t.info AAB301, plf.PLF004, plf.PLF005, plf.PLF006,
f_getcodename('AAC004',plf.PLF007) PLF007,PLF008,
  f_getcodename('AAC011',plf.PLF009) PLF009,
  f_getcodename('AAA004',plf.PLF010) PLF010,
  f_getcodename('AAA002',plf.PLF011) PLF011,
  f_getcodename('AAA003',plf.PLF012) PLF012,
    plf.PLF013,
  f_getcodename('AAA001',plf.PLF014) PLF014,
plf.AAE011, plf.AAE036,f_getcodename('EDC441',plf.PLF016) PLF016,
(case when plf.plf015 ='1' then '是' else '否' end) plf015,
  f_getcodename('AAA011',plf.PLF017) PLF017,
  f_getcodename('AAA016',plf.PLF018) PLF018,
  plf.PLF019, plf.PLF020, plf.PLF021, plf.PLF022,
  f_getcodename('EDC441',plf.plf023) plf023,
  f_getcodename('PLF024',plf.plf024) plf024,
  f_getcodename('PLF025',plf.plf025) plf025,plf.PLF026,
plf.DATASOURCE,
plf.AAE100,plf.UPDATEDATE,plf.UPDATEBY,plf.CREATEBY,plf.AAE012,plf.ISOVERAGE,plf.HELPYEAR,
  f_getcodename('EDC441',plf.phd013) phd013,
f_getcodename('AAA012',plf.phd014) phd014,plf.phd014_19

from poor_labor_force plf  ,POOR_XZQH t
where plf.AAB301=t.XZQHBM and plf.aae100='1'
/*待完善条件 劳动能力为空 转移就业、自主创业的，劳动能力是丧失劳动力、无劳动能力的要核实，未就业创业的要有就业创业意愿、未参加培训的要有培训教育意愿，劳动能力必填*/

  and (plf.plf018 is null or (plf011='1' and plf010 is null) or (plf024='6' and plf012 is null)) and plf.plf018 in
  ('01','02','05') and plf.PLF025='0' and plf.ispoor='1'


  and plf.ispoor='1'  /* and plf.plf018 in ('01','02')    and plf.ispoor='1'  and plf.plf025='0'  and plf.plf015 ='0'*/


  and (select count(1) from employment_situation where success='1' and eys002=plf.plf001 and rownum='1')= #{poorLaborForce.ishavejiuye,jdbcType=VARCHAR}


  and plf.PLF002 = #{poorLaborForce.plf002,jdbcType=VARCHAR}


  and plf.AAB301 like #{poorLaborForce.aab301,jdbcType=VARCHAR}


  and  plf.PLF004 like #{poorLaborForce.plf004,jdbcType=VARCHAR}


  and plf.PLF005 = #{poorLaborForce.plf005,jdbcType=VARCHAR}


  and plf.PLF006 = #{poorLaborForce.plf006,jdbcType=VARCHAR}


  and plf.PLF007 = #{poorLaborForce.plf007,jdbcType=VARCHAR}


  and to_number(plf.PLF008) >= to_number(#{poorLaborForce.startage,jdbcType=VARCHAR})


  and to_number(plf.PLF008) <= to_number(#{poorLaborForce.endage,jdbcType=VARCHAR})


  and plf.PLF009 = #{poorLaborForce.plf009,jdbcType=VARCHAR}


  and plf.PLF010 = #{poorLaborForce.plf010,jdbcType=VARCHAR}


  and plf.PLF011 = #{poorLaborForce.plf011,jdbcType=VARCHAR}


  and plf.PLF012 = #{poorLaborForce.plf012,jdbcType=VARCHAR}


  and plf.Phd014 = #{poorLaborForce.plf013,jdbcType=VARCHAR}


  and plf.phd014_19 = #{poorLaborForce.phd014_19,jdbcType=VARCHAR}


  and plf. plf001 in (select eys002 from  employment_situation e where
  plf.plf001=e.eys002
  and e.eys011 = #{poorLaborForce.eys011,jdbcType=VARCHAR}
  and e.success='1'
  )


  and plf.AAE011 = #{poorLaborForce.aae011,jdbcType=VARCHAR}


  and  plf.AAE036 = #{poorLaborForce.aae036,jdbcType=TIMESTAMP}


  and plf.PLF016 = #{poorLaborForce.plf016,jdbcType=VARCHAR}


  and plf.PLF015 = #{poorLaborForce.plf015,jdbcType=VARCHAR}


  and  plf.PLF017 = #{poorLaborForce.plf017,jdbcType=VARCHAR}


  and  plf.PLF018 = #{poorLaborForce.plf018,jdbcType=VARCHAR}


  and  plf.PLF019 = #{poorLaborForce.plf019,jdbcType=VARCHAR}


  and plf.PLF020 = #{poorLaborForce.plf020,jdbcType=VARCHAR}


  and  plf.PLF021 = #{poorLaborForce.plf021,jdbcType=VARCHAR}


  and  plf.PLF022 = #{poorLaborForce.plf022,jdbcType=VARCHAR}


  and  plf.PLF023 = #{poorLaborForce.plf023,jdbcType=VARCHAR}


  and plf.PLF024 = #{poorLaborForce.plf024,jdbcType=VARCHAR}


  and plf.PLF025 = #{poorLaborForce.plf025,jdbcType=VARCHAR}


  and  plf.DATASOURCE = #{poorLaborForce.datasource,jdbcType=VARCHAR}


  and  plf.AAE100 = #{poorLaborForce.aae100,jdbcType=VARCHAR}


  and  plf.UPDATEDATE = #{poorLaborForce.updatedate,jdbcType=TIMESTAMP}


  and  plf.UPDATEBY = #{poorLaborForce.updateby,jdbcType=VARCHAR}


  and  plf.CREATEBY = #{poorLaborForce.createby,jdbcType=VARCHAR}


  and  plf.AAE012 = #{poorLaborForce.aae012,jdbcType=VARCHAR}


  and plf.ISOVERAGE = #{poorLaborForce.isoverage,jdbcType=VARCHAR}


  and plf.HELPYEAR in #{poorLaborForce.helpyear,jdbcType=VARCHAR}


  and plf.phd013 = #{poorLaborForce.phd013,jdbcType=VARCHAR}


  and plf.plf015 ='1'

) a1
where rownum <= #{pageSize})
where rn >= #{startRecord}

select plf.PLF001,ph.PHD002 TSN003,ph.PHD003 PLF002, t.info AAB301, plf.PLF004, plf.PLF005,plf.PLF006, f_getcodename('AAC004',plf.PLF007) PLF007,PLF008, f_getcodename('AAC011',plf.PLF009) PLF009, f_getcodename('AAA004',plf.PLF010) PLF010, f_getcodename('AAA002',plf.PLF011) PLF011, f_getcodename('AAA003',plf.PLF012) PLF012, plf.PLF013, f_getcodename('AAA001',plf.PLF014) PLF014, plf.AAE011, plf.AAE036, plf.PLF016, (case when plf.plf015 ='1' then '是' else '否' end) plf015, f_getcodename('AAA011',plf.PLF017) PLF017, f_getcodename('AAA016',plf.PLF018) PLF018, plf.PLF019, plf.PLF020, plf.PLF021, plf.PLF022, f_getcodename('EDC441',plf.plf023) plf023, f_getcodename('PLF024',plf.plf024) plf024, f_getcodename('PLF025',plf.plf025) plf025,plf.PLF026, plf.DATASOURCE, plf.AAE100,plf.UPDATEDATE,plf.UPDATEBY,plf.CREATEBY,plf.AAE012,plf.ISOVERAGE,plf.HELPYEAR, f_getcodename('EDC441',ph.phd013) phd013 from poor_labor_force plf left join poor_households ph on plf.plf002=ph.phd001,POOR_XZQH t where plf.AAB301=t.XZQHBM and plf.aae100='1'
and plf.AAB301 like #{poorLaborForce.aab301,jdbcType=VARCHAR}
